binding.script:

var m = arg[0];
if (m < 0.01) return '0°';
if (m < 0.251) return '10°';
if (m < 0.501) return '20°';
if (m < 0.751) return '30°';
return '40°';

var m = arg[0];
if (m == 1) return 'RIGHT';
if (m == 2) return 'LEFT';
if (m == 3) return 'BOTH';
if (m == 4) return 'START !';
return 'OFF';
